    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        String rollno = roll.getText();
        s = people.search(rollno);
        
        if(s == null)
        {
            get_field1().setText(" ");
            get_field2().setText(" ");
            get_field3().setText(" ");
            get_field4().setText(" ");
            get_field5().setText(" ");
            get_field6().setText(" ");
            get_field7().setText(" ");
            get_field8().setText(" ");
            get_field9().setText(" ");
        }
        else{
        get_field1().setText(" " + s.ass1);
        get_field2().setText(" " + s.ass2);
        get_field3().setText(" " + s.ass3);
        get_field4().setText(" " + s.ass4);
        get_field5().setText(" " + s.ass5);
        get_field6().setText(" " + s.mid_marks);
        get_field7().setText(" " + s.end1);
        get_field8().setText(" " + s.end2);
        get_field9().setText(s.grade);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

